import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { GET_ERRORS, SET_CURRENT_USER } from './types';
import setAuthTokenInHeader from '../utils/setAuthTokenInHeader';


/**
 * @todo use Redux for the validation
 * @param User data
 * @returns boolean
 */
// validation (temp)

const validate = data => (dispatch) => {
	const {
		email, name, password, password2,
	} = data;
	if (email.length === 0) {
		const errMsg = { email: 'Please fill in your email.' };
		dispatch({
			type: GET_ERRORS,
			payload: errMsg,
		});
		return false;
	}
	if (name.length === 0) {
		const errMsg = { name: 'Please fill in your name.' };
		dispatch({
			type: GET_ERRORS,
			payload: errMsg,
		});
		return false;
	}
	if (name.length < 2 || name.length > 30) {
		const errMsg = { name: 'Name must be between 2 and 30 characters.' };
		dispatch({
			type: GET_ERRORS,
			payload: errMsg,
		});
		return false;
	}
	if (password.length === 0) {
		const errMsg = { password: 'Password is required.' };
		dispatch({
			type: GET_ERRORS,
			payload: errMsg,
		});
		return false;
	}
	if (password.length < 8) {
		const errMsg = { password: 'Password must be at least 8 character.' };
		dispatch({
			type: GET_ERRORS,
			payload: errMsg,
		});
		return false;
	}
	if (password2.length === 0) {
		const errMsg = { password2: 'Please type your password again.' };
		dispatch({
			type: GET_ERRORS,
			payload: errMsg,
		});
		return false;
	}
	if (password !== password2) {
		const errMsg = { password2: 'The two passwords don\'t match.' };
		dispatch({
			type: GET_ERRORS,
			payload: errMsg,
		});
		return false;
	}
	return true;
};

// Register User
export const registerUser = (userData, history) => (dispatch) => {
	const isValid = validate(userData)(dispatch);
	if (isValid) {
		delete userData.password2;
		sessionStorage.setItem('unactivatedEmail', userData.email);
		axios({
			method: 'post',
			url: '/users/register',
			data: userData,
		})
			.then(() => history.push('/verify-email'))
			.catch(err => dispatch({
				type: GET_ERRORS,
				payload: err.response.data,
			}));
	}
};

// Login and get the token
export const loginUser = userData => (dispatch) => {
	axios({
		method: 'post',
		url: '/users/login',
		data: userData,
	}).then((res) => {
		const { token } = res.data;
		// Save to localStorage
		localStorage.setItem('jwtToken', token);
		// Set to axios global header
		setAuthTokenInHeader(token);
		const decoded = jwt_decode(token);
		dispatch(setCurrentUser(decoded));
	})
		.catch(err => dispatch({
			type: GET_ERRORS,
			payload: err.response.data,
		}));
};

// install logged-in user
export const setCurrentUser = decoded => ({
	type: SET_CURRENT_USER,
	payload: decoded,
});

// log out
export const logoutUser = () => (dispatch) => {
	localStorage.removeItem('jwtToken');
	// Remove auth header from global
	setAuthTokenInHeader(false);
	// Set current user to {} which will set isAuthenticated to false as well
	dispatch(setCurrentUser({}));
	// return to home page
	window.open('/', '_self');
};

import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import FormHelperText from '@material-ui/core/FormHelperText';
import React from 'react';
import PropTypes from 'prop-types';

export default function KFAccountInput(props) {
	const {
		className, error, id, value, name, type, onChange, onKeyDown
	} = props;
	return (
		<FormControl className={className} error={!!error} aria-describedby={`${id}-helper-text`}>
			<InputLabel htmlFor={`${id}-helper`}>{name}</InputLabel>
			<Input id={id} value={value} type={type} onChange={onChange} onKeyDown={onKeyDown}/>
			{error && <FormHelperText id={`${id}-helper-text`}>{error}</FormHelperText>}
		</FormControl>
	);
}

KFAccountInput.propTypes = {
	className: PropTypes.string.isRequired,
	error: PropTypes.object,
	id: PropTypes.string.isRequired,
	value: PropTypes.string.isRequired,
	name: PropTypes.string.isRequired,
	type: PropTypes.string.isRequired,
	onChange: PropTypes.func.isRequired,
	onKeyDown: PropTypes.func
};

