function MenuItems(props) {
  const handleClose = props.closeHandle;
  if (props.isAuthenticated) {
    return (
      <span>
			<MenuItem component={Link} to={"/account"} onClick={handleClose}>
			My account
			</MenuItem>

			<MenuItem onClick={props.logoutUser}>
			<div onClick={handleClose}>Log out</div>
			</MenuItem>
			</span>
    );
  } else {
    return (
      <span>
        <MenuItem component={Link} to="/login" onClick={handleClose} >
          Login in
			</MenuItem>

			<MenuItem component={Link} to={"/register"} onClick={handleClose}>
			New here?
			</MenuItem>
			</span>
    );
  }
}

class headerPageIndex extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      anchorEl: null,
      cartNumber: 0,
    };
  }

  componentDidMount() {
    const requestURL = `/cart`;

    Axios.get(requestURL)
      .then((response) => {
      this.setState({ cartNumber: response.data.length });
    }).catch((error) => {
      console.log(error);
    });
  }

  handleClick = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { anchorEl } = this.state;
    const {
      background,
      containerDiv,
      logoPart,
      iconLogo,
      searchIcon,
      rightIcon,
      avatarStyle
    } = style;

    return (
      <div style={background}>
        <div style={containerDiv}>
          <div style={logoPart}>
            <img src={Logo} style={iconLogo} />
            <div style={searchIcon}>
              <SearchInput/>
              <Icon icon={ic_search} size={24} style={{ marginBottom: '8px' }} />
            </div>
            <div style={rightIcon}>
              <div style={avatarStyle}
                   onClick={this.handleClick}
              ><Icon icon={ic_account_circle} size={24} />
              </div>
              <Menu
                id="simple-menu"
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={this.handleClose}
              >
                <MenuItems
                  isAuthenticated={this.props.auth.isAuthenticated}
                  closeHandle={this.handleClose}
                  logoutUser={this.props.logoutUser}
                />
              </Menu>
              <Cart/>
            </div>
          </div>
        </div>
        <NavigationBar/>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { loginUser, logoutUser })(headerPageIndex);

